import axios from 'axios';
import React, { useState, useEffect } from 'react';
import jwt_decode from 'jwt-decode';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [name, setName] = useState('');
  const [token, setToken] = useState('');
  const [expire, setExpire] = useState('');
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    refreshToken(); // Memanggil refreshToken langsung di dalam useEffect
  getUsers()
    // Tidak perlu menambahkan refreshToken ke dalam array dependensi
    // Karena tidak digunakan di dalam useEffect ini
  }, []);
    const refreshToken = async () => {
      try {
        const response = await axios.get('http://localhost:4500/token');
        setToken(response.data.accessToken);
        const decoded = jwt_decode(response.data.accessToken);
        setName(decoded.name);
        setExpire(decoded.exp);
      } catch (error) {
        if (error.response) {
          navigate('/');
        }
      }
    };


  const axiosJWT = axios.create();
axiosJWT.interceptors.request.use(async(config) =>{
  const currentDate = new Date();
  if (expire * 1000 < currentDate.getTime()) {
    const response = await axios.get('http://localhost:4500/token')
    config.headers.Authorization = `Bearer ${response.data.accessToken}`
    setToken(response.data.accessToken)
    const decoded = jwt_decode(response.data.accessToken);
        setName(decoded.name);
        setExpire(decoded.exp);
  }
  return config;
}, (error)=>{
  return Promise.reject(error)
})
  const getUsers = async () => {
    try {
      const response = await axiosJWT.get('http://localhost:4500/users', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUsers(response.data);
    } catch (error) {
      if (error.response) {
        navigate('/');
      }
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="title">Welcome Back: {name}</h1>
      <button onClick={getUsers} className="button is-info">
        Get Users
      </button>
      <table className="table is-bordered is-fullwidth">
      <thead>
        <tr>
          <th>No</th>
          <th>Nama</th>
          <th>Email</th>
        </tr>
      </thead>
      <tbody>
        {users.map((user, index) => (
          <tr key={user.id}>
            <td>{index }</td>
            <td>{user.name}</td>
            <td>{user.email}</td>
          </tr>
        ))}
      </tbody>
    </table>
    </div>
  );
};

export default Dashboard;
